// src/components/pages/Historique.js
import React, { useState, useEffect } from 'react';
import { LineChart, Line, CartesianGrid, XAxis, YAxis, Tooltip, ResponsiveContainer } from 'recharts';
import './Historique.css';

const StatisticsCard = ({ title, value, icon, color }) => (
  <div className="statistics-card">
    <div className="stat-header">
      <div className="stat-icon" style={{ backgroundColor: color }}>
        {icon}
      </div>
      <div className="stat-info">
        <h3>{title}</h3>
        <div className="stat-value">{value}</div>
      </div>
    </div>
  </div>
);

const Historique = () => {
  const [selectedPeriod, setSelectedPeriod] = useState('7d');
  const [selectedMetric, setSelectedMetric] = useState('humidity');
  const [data, setData] = useState([]);
  const [statistics, setStatistics] = useState({});
  const [isLoading, setIsLoading] = useState(true);

  // G√©n√©rer des donn√©es d'exemple
  const generateSampleData = (period) => {
    const days = period === '7d' ? 7 : period === '30d' ? 30 : 1;
    const data = [];
    const now = new Date();

    for (let i = days - 1; i >= 0; i--) {
      const date = new Date(now.getTime() - i * 24 * 60 * 60 * 1000);
      data.push({
        date: date.toLocaleDateString('fr-FR', { month: 'short', day: 'numeric' }),
        fullDate: date.toISOString().split('T')[0],
        humidity: Math.floor(30 + Math.random() * 40), // Humidit√© de l'air
        moisture: Math.floor(20 + Math.random() * 60), // Humidit√© du sol
        temperature: Math.floor(18 + Math.random() * 15) // Temp√©rature
      });
    }
    return data;
  };

  // Calculer les moyennes des capteurs
  const calculateStatistics = (data) => {
    if (!data || data.length === 0) {
      return {
        averageHumidity: { value: '--' },
        averageMoisture: { value: '--' },
        averageTemperature: { value: '--' }
      };
    }

    const totalHumidity = data.reduce((sum, item) => sum + item.humidity, 0);
    const totalMoisture = data.reduce((sum, item) => sum + item.moisture, 0);
    const totalTemperature = data.reduce((sum, item) => sum + item.temperature, 0);

    const avgHumidity = Math.round(totalHumidity / data.length);
    const avgMoisture = Math.round(totalMoisture / data.length);
    const avgTemperature = Math.round(totalTemperature / data.length);

    return {
      averageHumidity:    { value: {avgHumidity} },
      averageMoisture:    { value: {avgMoisture} },
      averageTemperature: { value: {avgTemperature} }
    };
  };

  useEffect(() => {
    setIsLoading(true);
    // Simuler un appel API
    setTimeout(() => {
      const newData = generateSampleData(selectedPeriod);
      setData(newData);
      setStatistics(calculateStatistics(newData));
      setIsLoading(false);
    }, 1000);
  }, [selectedPeriod]);

  if (isLoading) {
    return (
      <div className="loading-container">
        <div className="loading-spinner">üìä</div>
        <p>Chargement de l'historique...</p>
      </div>
    );
  }

  return (
    <main className="historique-page">
      <header className="page-header">
        <h1>Historique des Capteurs</h1>
        <p className="page-subtitle">Analyse des donn√©es des capteurs sur la p√©riode s√©lectionn√©e</p>
        
        <div className="controls">
          <div className="period-selector">
            <button 
              className={selectedPeriod === '24h' ? 'active' : ''}
              onClick={() => setSelectedPeriod('24h')}
            >
              24 Heures
            </button>
            <button 
              className={selectedPeriod === '7d' ? 'active' : ''}
              onClick={() => setSelectedPeriod('7d')}
            >
              7 Jours
            </button>
            <button 
              className={selectedPeriod === '30d' ? 'active' : ''}
              onClick={() => setSelectedPeriod('30d')}
            >
              30 Jours
            </button>
          </div>
        </div>
      </header>

      {/* Statistics Overview - Moyennes des capteurs */}
      <section className="statistics-overview">
        <h2>Moyennes de la P√©riode</h2>
        <div className="statistics-grid">
          <StatisticsCard
            title="Humidit√© de l'Air Moyenne"
            value={statistics.averageHumidity?.value}
            icon="üí®"
            color="#2196f3"
          />
          <StatisticsCard
            title="Humidit√© du Sol Moyenne"
            value={statistics.averageMoisture?.value}
            icon="üíß"
            color="#4caf50"
          />
          <StatisticsCard
            title="Temp√©rature Moyenne"
            value={statistics.averageTemperature?.value}
            icon="üå°"
            color="#ff9800"
          />
        </div>
      </section>

      {/* Charts Section */}
      <section className="charts-section">
        <div className="chart-container">
          <div className="chart-header">
            <h2>√âvolution des Capteurs</h2>
            <div className="metric-selector">
              <button 
                className={selectedMetric === 'humidity' ? 'active' : ''}
                onClick={() => setSelectedMetric('humidity')}
              >
                Humidit√© Air
              </button>
              <button 
                className={selectedMetric === 'moisture' ? 'active' : ''}
                onClick={() => setSelectedMetric('moisture')}
              >
                Humidit√© Sol
              </button>
              <button 
                className={selectedMetric === 'temperature' ? 'active' : ''}
                onClick={() => setSelectedMetric('temperature')}
              >
                Temp√©rature
              </button>
            </div>
          </div>
          
          <div className="chart-wrapper">
            <ResponsiveContainer width="100%" height={400}>
              <LineChart data={data}>
                <CartesianGrid stroke="#f0f0f0" strokeDasharray="2 2" />
                <XAxis dataKey="date" tick={{ fontSize: 12 }} />
                <YAxis tick={{ fontSize: 12 }} />
                <Tooltip 
                  contentStyle={{ 
                    backgroundColor: '#fff', 
                    border: '1px solid #ccc',
                    borderRadius: '8px'
                  }}
                />
                <Line 
                  type="monotone" 
                  dataKey={selectedMetric} 
                  stroke={selectedMetric === 'humidity' ? '#2196f3' : 
                          selectedMetric === 'moisture' ? '#4caf50' : '#ff9800'} 
                  strokeWidth={3}
                  dot={{ 
                    fill: selectedMetric === 'humidity' ? '#2196f3' : 
                          selectedMetric === 'moisture' ? '#4caf50' : '#ff9800', 
                    strokeWidth: 2, 
                    r: 4 
                  }}
                  activeDot={{ r: 6, strokeWidth: 2 }}
                  name={selectedMetric === 'humidity' ? 'Humidit√© Air (%)' : 
                        selectedMetric === 'moisture' ? 'Humidit√© Sol (%)' : 'Temp√©rature (¬∞C)'}
                />
              </LineChart>
            </ResponsiveContainer>
          </div>
        </div>
      </section>
    </main>
  );
};

export default Historique;
