// src/components/pages/Historique.js
import React, { useState, useEffect } from 'react';
import { LineChart, Line, BarChart, Bar, CartesianGrid, XAxis, YAxis, Tooltip, ResponsiveContainer, PieChart, Pie, Cell } from 'recharts';
import './Historique.css';

const IrrigationEvent = ({ event }) => (
  <div className="irrigation-event">
    <div className="event-icon">
      <img src="/api/placeholder/40/40" alt="irrigation" />
    </div>
    <div className="event-details">
      <div className="event-time">{event.time}</div>
      <div className="event-info">
        <span>Durée: {event.duration}</span>
        <span>Eau utilisée: {event.waterUsed}</span>
        <span>Humidité avant: {event.humidityBefore}%</span>
      </div>
    </div>
    <div className="event-trigger">
      <span className={`trigger-badge ${event.trigger}`}>
        {event.trigger === 'automatic' ? 'Automatique' : 
         event.trigger === 'manual' ? 'Manuel' : 'Programmé'}
      </span>
    </div>
  </div>
);

const StatisticsCard = ({ title, value, change, icon, color }) => (
  <div className="statistics-card">
    <div className="stat-header">
      <div className="stat-icon" style={{ backgroundColor: color }}>
        {icon}
      </div>
      <div className="stat-info">
        <h3>{title}</h3>
        <div className="stat-value">{value}</div>
      </div>
    </div>
    <div className="stat-change">
      <span className={`change ${change > 0 ? 'positive' : 'negative'}`}>
        {change > 0 ? '↗' : '↘'} {Math.abs(change)}%
      </span>
      <span className="change-label">vs semaine dernière</span>
    </div>
  </div>
);

const Historique = () => {
  const [selectedPeriod, setSelectedPeriod] = useState('7d');
  const [selectedMetric, setSelectedMetric] = useState('humidity');
  const [data, setData] = useState([]);
  const [irrigationEvents, setIrrigationEvents] = useState([]);
  const [statistics, setStatistics] = useState({});
  const [isLoading, setIsLoading] = useState(true);

  // Sample historical data
  const generateSampleData = (period) => {
    const days = period === '7d' ? 7 : period === '30d' ? 30 : 1;
    const data = [];
    const now = new Date();

    for (let i = days - 1; i >= 0; i--) {
      const date = new Date(now.getTime() - i * 24 * 60 * 60 * 1000);
      data.push({
        date: date.toLocaleDateString('fr-FR', { month: 'short', day: 'numeric' }),
        fullDate: date.toISOString().split('T')[0],
        humidity: Math.floor(30 + Math.random() * 40),
        temperature: Math.floor(18 + Math.random() * 15),
        waterUsed: Math.floor(80 + Math.random() * 200),
        lightLevel: Math.floor(200 + Math.random() * 800)
      });
    }
    return data;
  };

  const generateIrrigationEvents = () => {
    const events = [];
    const triggers = ['automatic', 'manual', 'scheduled'];
    
    for (let i = 0; i < 12; i++) {
      const date = new Date();
      date.setHours(date.getHours() - i * 6);
      
      events.push({
        id: i + 1,
        time: date.toLocaleString('fr-FR'),
        duration: `${Math.floor(15 + Math.random() * 20)} min`,
        waterUsed: `${Math.floor(100 + Math.random() * 150)} L`,
        humidityBefore: Math.floor(20 + Math.random() * 25),
        trigger: triggers[Math.floor(Math.random() * triggers.length)]
      });
    }
    return events;
  };

  const generateStatistics = () => {
    return {
      totalWaterUsed: { value: '2,450 L', change: -12 },
      averageHumidity: { value: '42%', change: +8 },
      irrigationFrequency: { value: '3.2/jour', change: -5 },
      systemEfficiency: { value: '94%', change: +3 }
    };
  };

  useEffect(() => {
    setIsLoading(true);
    // Simulate API call
    setTimeout(() => {
      setData(generateSampleData(selectedPeriod));
      setIrrigationEvents(generateIrrigationEvents());
      setStatistics(generateStatistics());
      setIsLoading(false);
    }, 1000);
  }, [selectedPeriod]);

  // Data for efficiency pie chart
  const efficiencyData = [
    { name: 'Irrigation Optimale', value: 78, color: '#4caf50' },
    { name: 'Sur-irrigation', value: 15, color: '#ff9800' },
    { name: 'Sous-irrigation', value: 7, color: '#f44336' }
  ];

  const renderCustomizedLabel = ({ cx, cy, midAngle, innerRadius, outerRadius, percent }) => {
    const RADIAN = Math.PI / 180;
    const radius = innerRadius + (outerRadius - innerRadius) * 0.5;
    const x = cx + radius * Math.cos(-midAngle * RADIAN);
    const y = cy + radius * Math.sin(-midAngle * RADIAN);

    return (
      <text 
        x={x} 
        y={y} 
        fill="white" 
        textAnchor={x > cx ? 'start' : 'end'} 
        dominantBaseline="central"
        fontSize="12"
        fontWeight="bold"
      >
        {`${(percent * 100).toFixed(0)}%`}
      </text>
    );
  };

  if (isLoading) {
    return (
      <div className="loading-container">
        <div className="loading-spinner">📊</div>
        <p>Chargement de l'historique...</p>
      </div>
    );
  }

  return (
    <main className="historique-page">
      <header className="page-header">
        <h1>Historique d'Irrigation</h1>
        <p className="page-subtitle">Analyse et suivi des performances du système</p>
        
        <div className="controls">
          <div className="period-selector">
            <button 
              className={selectedPeriod === '24h' ? 'active' : ''}
              onClick={() => setSelectedPeriod('24h')}
            >
              24 Heures
            </button>
            <button 
              className={selectedPeriod === '7d' ? 'active' : ''}
              onClick={() => setSelectedPeriod('7d')}
            >
              7 Jours
            </button>
            <button 
              className={selectedPeriod === '30d' ? 'active' : ''}
              onClick={() => setSelectedPeriod('30d')}
            >
              30 Jours
            </button>
          </div>
        </div>
      </header>

      {/* Statistics Overview */}
      <section className="statistics-overview">
        <h2>Statistiques de la Période</h2>
        <div className="statistics-grid">
          <StatisticsCard
            title="Consommation d'Eau"
            value={statistics.totalWaterUsed?.value}
            change={statistics.totalWaterUsed?.change}
            icon="💧"
            color="#2196f3"
          />
          <StatisticsCard
            title="Humidité Moyenne"
            value={statistics.averageHumidity?.value}
            change={statistics.averageHumidity?.change}
            icon="🌱"
            color="#4caf50"
          />
          <StatisticsCard
            title="Fréquence d'Irrigation"
            value={statistics.irrigationFrequency?.value}
            change={statistics.irrigationFrequency?.change}
            icon="⏰"
            color="#ff9800"
          />
          <StatisticsCard
            title="Efficacité Système"
            value={statistics.systemEfficiency?.value}
            change={statistics.systemEfficiency?.change}
            icon="⚡"
            color="#9c27b0"
          />
        </div>
      </section>

      {/* Charts Section */}
      <section className="charts-section">
        <div className="chart-container">
          <div className="chart-header">
            <h2>Évolution des Mesures</h2>
            <div className="metric-selector">
              <button 
                className={selectedMetric === 'humidity' ? 'active' : ''}
                onClick={() => setSelectedMetric('humidity')}
              >
                Humidité
              </button>
              <button 
                className={selectedMetric === 'temperature' ? 'active' : ''}
                onClick={() => setSelectedMetric('temperature')}
              >
                Température
              </button>
              <button 
                className={selectedMetric === 'light' ? 'active' : ''}
                onClick={() => setSelectedMetric('light')}
              >
                Luminosité
              </button>
            </div>
          </div>
          
          <div className="chart-wrapper">
            <ResponsiveContainer width="100%" height={300}>
              <LineChart data={data}>
                <CartesianGrid stroke="#f0f0f0" strokeDasharray="2 2" />
                <XAxis dataKey="date" tick={{ fontSize: 12 }} />
                <YAxis tick={{ fontSize: 12 }} />
                <Tooltip 
                  contentStyle={{ 
                    backgroundColor: '#fff', 
                    border: '1px solid #ccc',
                    borderRadius: '8px'
                  }}
                />
                <Line 
                  type="monotone" 
                  dataKey={selectedMetric === 'humidity' ? 'humidity' : 
                          selectedMetric === 'temperature' ? 'temperature' : 'lightLevel'} 
                  stroke={selectedMetric === 'humidity' ? '#1a562f' : 
                          selectedMetric === 'temperature' ? '#ff9800' : '#2196f3'} 
                  strokeWidth={3}
                  dot={{ fill: selectedMetric === 'humidity' ? '#1a562f' : 
                              selectedMetric === 'temperature' ? '#ff9800' : '#2196f3', 
                        strokeWidth: 2, r: 4 }}
                  activeDot={{ r: 6, strokeWidth: 2 }}
                />
              </LineChart>
            </ResponsiveContainer>
          </div>
        </div>

        <div className="chart-container">
          <h2>Consommation d'Eau Quotidienne</h2>
          <div className="chart-wrapper">
            <ResponsiveContainer width="100%" height={300}>
              <BarChart data={data}>
                <CartesianGrid stroke="#f0f0f0" strokeDasharray="2 2" />
                <XAxis dataKey="date" tick={{ fontSize: 12 }} />
                <YAxis tick={{ fontSize: 12 }} />
                <Tooltip 
                  contentStyle={{ 
                    backgroundColor: '#fff', 
                    border: '1px solid #ccc',
                    borderRadius: '8px'
                  }}
                />
                <Bar 
                  dataKey="waterUsed" 
                  fill="#1a562f" 
                  radius={[4, 4, 0, 0]}
                />
              </BarChart>
            </ResponsiveContainer>
          </div>
        </div>
      </section>

      {/* Efficiency Analysis */}
      <section className="efficiency-section">
        <div className="efficiency-chart">
          <h2>Analyse d'Efficacité</h2>
          <div className="chart-wrapper">
            <ResponsiveContainer width="100%" height={250}>
              <PieChart>
                <Pie
                  data={efficiencyData}
                  cx="50%"
                  cy="50%"
                  labelLine={false}
                  label={renderCustomizedLabel}
                  outerRadius={80}
                  fill="#8884d8"
                  dataKey="value"
                >
                  {efficiencyData.map((entry, index) => (
                    <Cell key={`cell-${index}`} fill={entry.color} />
                  ))}
                </Pie>
                <Tooltip />
              </PieChart>
            </ResponsiveContainer>
          </div>
          <div className="efficiency-legend">
            {efficiencyData.map((item, index) => (
              <div key={index} className="legend-item">
                <div 
                  className="legend-color" 
                  style={{ backgroundColor: item.color }}
                ></div>
                <span>{item.name}: {item.value}%</span>
              </div>
            ))}
          </div>
        </div>

        <div className="insights-panel">
          <h3>🎯 Insights et Recommandations</h3>
          <div className="insights-list">
            <div className="insight-item positive">
              <span className="insight-icon">✅</span>
              <p>Excellente efficacité d'irrigation (94%)</p>
            </div>
            <div className="insight-item warning">
              <span className="insight-icon">⚠️</span>
              <p>Pic de consommation détecté le 15/01 - vérifier les paramètres</p>
            </div>
            <div className="insight-item info">
              <span className="insight-icon">💡</span>
              <p>Économie de 12% par rapport à la semaine dernière</p>
            </div>
            <div className="insight-item positive">
              <span className="insight-icon">📈</span>
              <p>Tendance d'amélioration continue sur 30 jours</p>
            </div>
          </div>
        </div>
      </section>

      {/* Irrigation Events */}
      <section className="events-section">
        <h2>Événements d'Irrigation Récents</h2>
        <div className="events-list">
          {irrigationEvents.slice(0, 8).map(event => (
            <IrrigationEvent key={event.id} event={event} />
          ))}
        </div>
        
        <div className="events-footer">
          <button className="btn btn-secondary">
            📄 Exporter les Données
          </button>
          <button className="btn btn-primary">
            📊 Rapport Détaillé
          </button>
        </div>
      </section>
    </main>
  );
};

export default Historique;
