import React, { useState, useEffect } from 'react';
import { LineChart, Line, CartesianGrid, XAxis, YAxis, Tooltip, BarChart, Bar, ResponsiveContainer } from 'recharts';
import io from 'socket.io-client';
import './DashboardHome.css';

const StatCard = ({ title, value, icon, status = 'normal' }) => (
  <div className={`stat-card ${status}`}>
    <div className="stat-icon">{icon}</div>
    <h4>{title}</h4>
    <p><strong>{value}</strong></p>
  </div>
);

const DashboardHome = () => {
  const [chartData, setChartData] = useState([]);
  const [currentStats, setCurrentStats] = useState({
    humidity: 0,
    temperature: 0,
    waterUsed: 0
  });
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState(null);
  const [connectionStatus, setConnectionStatus] = useState('disconnected');

  // Initialize Socket.IO connection
  useEffect(() => {
    const socket = io('http://localhost:3000', {
      reconnection: true,
      reconnectionAttempts: 5,
      reconnectionDelay: 1000,
    });

    // Handle Socket.IO connection
    socket.on('connect', () => {
      console.log('‚úÖ Connected to Socket.IO server');
      setConnectionStatus('connected');
      setError(null);
      setIsLoading(false);
    });

    // Handle sensor data from server
    socket.on('sensorData', (sensorData) => {
      console.log('Data received from server:', sensorData);
      
      if (sensorData && typeof sensorData === 'object') {
        // Update current stats
        setCurrentStats({
          moisure: sensorData.h_soil_pourcentage || "--", // Match MQTT data field 'h' for humidity
          humidity: sensorData.h_air || "--", // Match MQTT data field 'h' for humidity
          temperature: sensorData.t || "--", // Match MQTT data field 't' for temperature
          waterUsed: Math.floor(Math.random() * 300) // Placeholder until real data available
        });

        // Create chart data point
        const now = new Date();
        const timeString = now.toLocaleTimeString().slice(0, 5);

        const newDataPoint = {
          time: timeString,
          humidity: sensorData.h || 0,
          temperature: sensorData.t || 0,
          water: Math.floor(Math.random() * 300), // Placeholder
          timestamp: sensorData.ts ? new Date(sensorData.ts * 1000).toISOString() : now.toISOString()
        };

        // Add to chart data (keep last 10 points)
        setChartData(prevData => {
          const updatedData = [...prevData, newDataPoint];
          return updatedData.slice(-10); // Keep only last 10 points
        });

        console.log('‚úÖ Data updated successfully');
      } else {
        console.log('‚ö†Ô∏è Invalid data received from server');
        setError('Invalid data received from server');
      }
    });

    // Handle connection errors
    socket.on('connect_error', (error) => {
      console.error('‚ùå Socket.IO connection error:', error);
      setConnectionStatus('disconnected');
      setError('Failed to connect to server');
      setIsLoading(false);

      // Use fallback data if no real data available
      if (chartData.length === 0) {
        const fallbackData = createFallbackData();
        setChartData(fallbackData);
        setCurrentStats({
          humidity: 45,
          temperature: 23,
          waterUsed: 150
        });
      }
    });

    // Handle disconnection
    socket.on('disconnect', () => {
      console.log('üîå Disconnected from Socket.IO server');
      setConnectionStatus('disconnected');
      setError('Server disconnected');
    });

    // Initial load: check if server is running
    const loadInitialData = async () => {
      setIsLoading(true);
      if (!socket.connected) {
        console.log('‚ö†Ô∏è Server not running, using fallback data');
        setError('Server not running (Start: node server.js)');
        const fallbackData = createFallbackData();
        setChartData(fallbackData);
        setCurrentStats({
          humidity: 45,
          temperature: 23,
          waterUsed: 150
        });
        setIsLoading(false);
      }
    };

    loadInitialData();

    // Cleanup on unmount
    return () => {
      socket.disconnect();
      console.log('üßπ Socket.IO connection closed');
    };
  }, []);

  // Fallback data when server is not available
  const createFallbackData = () => {
    const data = [];
    const now = new Date();
    
    for (let i = 9; i >= 0; i--) {
      const time = new Date(now.getTime() - i * 300000);
      data.push({
        time: time.toLocaleTimeString().slice(0, 5),
        humidity: Math.floor(30 + Math.random() * 50),
        temperature: Math.floor(15 + Math.random() * 15),
        water: Math.floor(Math.random() * 300),
        timestamp: time.toISOString()
      });
    }
    return data;
  };

  // Get status colors for stat cards
  const getmoisureStatus = () => {
    if (currentStats.moisure < 20) return 'danger';
    if (currentStats.moisure < 30) return 'warning';
    return 'normal';
  };

  // Get status colors for stat cards
  const getHumidityStatus = () => {
    if (currentStats.humidity < 20) return 'danger';
    if (currentStats.humidity < 30) return 'warning';
    return 'normal';
  };

  const getTemperatureStatus = () => {
    if (currentStats.temperature > 35) return 'warning';
    if (currentStats.temperature > 40) return 'danger';
    return 'normal';
  };

  // Show loading
  if (isLoading) {
    return (
      <div className="loading-container" style={{ padding: '50px', textAlign: 'center' }}>
        <div className="loading-spinner">‚è≥</div>
        <p>Chargement des donn√©es du serveur...</p>
      </div>
    );
  }

  return (
    <main className="dashboard-home">
      {/* Header with connection status */}
      <header className="dashboard-header">
        <h1>Syst√®me d'Irrigation Intelligent</h1>
        <div className={`connection-status ${connectionStatus}`}>
          <span className="status-dot">
            {connectionStatus === 'connected' ? 'üü¢' : 'üî¥'}
          </span>
          <span className="status-text">
            {connectionStatus === 'connected' ? 'Serveur connect√©' : 'Serveur d√©connect√©'}
          </span>
        </div>
      </header>

      {/* Error message */}
      {error && (
        <div className="error-banner" style={{ 
          background: '#ffebee', 
          color: '#c62828', 
          padding: '10px', 
          borderRadius: '5px', 
          margin: '10px 0',
          display: 'flex',
          justifyContent: 'space-between',
          alignItems: 'center'
        }}>
          <span>‚ö†Ô∏è {error}</span>
          <button 
            onClick={() => window.location.reload()} 
            className="retry-button"
            style={{
              background: '#c62828',
              color: 'white',
              border: 'none',
              padding: '5px 10px',
              borderRadius: '3px',
              cursor: 'pointer'
            }}
          >
            R√©essayer
          </button>
        </div>
      )}

      {/* Stats Cards */}
      <section className="stats-section" style={{ 
        display: 'grid', 
        gridTemplateColumns: 'repeat(auto-fit, minmax(250px, 1fr))', 
        gap: '20px', 
        margin: '20px 0' 
      }}>
        <StatCard 
          title="Humidit√© du sol" 
          value={`${currentStats.moisure}%`} 
          icon="üíß"
          status={getmoisureStatus()}
        />
        <StatCard 
          title="Temp√©rature" 
          value={`${currentStats.temperature}¬∞C`} 
          icon="üå°Ô∏è"
          status={getTemperatureStatus()}
        />
        <StatCard 
          title="Humidit√© de l'air" 
          value={`${currentStats.humidity}%`} 
          icon="üíß"
          status={getHumidityStatus()}
        />
        <StatCard 
          title="Niveau d'Irrigation" 
          value={currentStats.moisure < 30 ? 'danger' : '√âlev√©'} 
          icon="üå±"
          status="normal"
        />
        <StatCard 
          title="Consommation d'Eau" 
          value={`${currentStats.waterUsed}L`} 
          icon="üö∞"
          status="normal"
        />
      </section>

      {/* Charts */}
      {chartData.length > 0 && (
        <>
          <section className="chart-section" style={{ margin: '30px 0' }}>
            <h2>√âvolution de l'humidit√© et temp√©rature</h2>
            <div className="chart-container" style={{ height: '300px', margin: '20px 0' }}>
              <ResponsiveContainer width="100%" height={300}>
                <LineChart data={chartData}>
                  <CartesianGrid stroke="#eee" strokeDasharray="3 3" />
                  <XAxis dataKey="time" />
                  <YAxis yAxisId="left" orientation="left" stroke="#1a562f" />
                  <YAxis yAxisId="right" orientation="right" stroke="#8884d8" />
                  <Tooltip />
                  <Line 
                    yAxisId="left" 
                    type="monotone" 
                    dataKey="humidity" 
                    stroke="#1a562f" 
                    strokeWidth={2}
                    name="Humidit√© (%)"
                  />
                  <Line 
                    yAxisId="right" 
                    type="monotone" 
                    dataKey="temperature" 
                    stroke="#8884d8" 
                    strokeWidth={2}
                    name="Temp√©rature (¬∞C)"
                  />
                </LineChart>
              </ResponsiveContainer>
            </div>
          </section>

          <section className="chart-section" style={{ margin: '30px 0' }}>
            <h2>Consommation d'eau</h2>
            <div className="chart-container" style={{ height: '300px', margin: '20px 0' }}>
              <ResponsiveContainer width="100%" height={300}>
                <BarChart data={chartData}>
                  <CartesianGrid stroke="#eee" strokeDasharray="3 3" />
                  <XAxis dataKey="time" />
                  <YAxis />
                  <Tooltip />
                  <Bar 
                    dataKey="water" 
                    fill="#1a562f" 
                    radius={[4, 4, 0, 0]}
                    name="Eau consomm√©e (L)"
                  />
                </BarChart>
              </ResponsiveContainer>
            </div>
          </section>
        </>
      )}

      {/* Instructions for ESP32 setup */}
      <section className="developer-info" style={{ 
        background: '#f5f5f5', 
        padding: '20px', 
        borderRadius: '5px', 
        marginTop: '30px' 
      }}>
        <details>
          <summary style={{ cursor: 'pointer', fontWeight: 'bold' }}>
            üìö Instructions pour ESP32 (pour ton coll√®gue)
          </summary>
          <div style={{ marginTop: '15px' }}>
            <h4>Configuration MQTT ESP32:</h4>
            <ul>
              <li><strong>MQTT Broker:</strong> 192.168.1.21:1883 (Mosquitto)</li>
              <li><strong>Topic:</strong> sensor/data</li>
              <li><strong>Format JSON:</strong></li>
            </ul>
            <pre style={{ 
              background: '#333', 
              color: '#fff', 
              padding: '10px', 
              borderRadius: '5px',
              overflow: 'auto'
            }}>{`{
  "sensorId": "sensor1",
  "t": 23,
  "h": 45,
  "m": 66,
  "ts": 2287866
}`}</pre>
            <h4>Exemple code ESP32:</h4>
            <pre style={{ 
              background: '#333', 
              color: '#fff', 
              padding: '10px', 
              borderRadius: '5px',
              overflow: 'auto'
            }}>{`#include <WiFi.h>
#include <PubSubClient.h>

WiFiClient espClient;
PubSubClient client(espClient);

void setup() {
  client.setServer("192.168.1.21", 1883);
}

void publishSensorData() {
  String payload = "{\\"sensorId\\":\\"sensor1\\"," +
                   "\\"t\\":" + String(temperature) + 
                   ",\\"h\\":" + String(humidity) + 
                   ",\\"m\\":" + String(moisture) +
                   ",\\"ts\\":" + String(millis()/1000) + "}";
  
  client.publish("sensor/data", payload.c_str());
}`}</pre>
          </div>
        </details>
      </section>
    </main>
  );
};

export default DashboardHome;
