// src/components/pages/Configurations.js
import React, { useState, useEffect } from 'react';
import './Configurations.css';

const ConfigSection = ({ title, children, icon }) => (
  <div className="config-section">
    <div className="section-header">
      <div className="section-icon">{icon}</div>
      <h3>{title}</h3>
    </div>
    <div className="section-content">
      {children}
    </div>
  </div>
);

const SettingItem = ({ label, children, description }) => (
  <div className="setting-item">
    <div className="setting-info">
      <label className="setting-label">{label}</label>
      {description && <p className="setting-description">{description}</p>}
    </div>
    <div className="setting-control">
      {children}
    </div>
  </div>
);

const Toggle = ({ checked, onChange, disabled = false }) => (
  <label className={`toggle ${disabled ? 'disabled' : ''}`}>
    <input 
      type="checkbox" 
      checked={checked} 
      onChange={onChange}
      disabled={disabled}
    />
    <span className="toggle-slider"></span>
  </label>
);

const Configurations = () => {
  const [config, setConfig] = useState({
    // Irrigation Settings
    autoIrrigation: true,
    humidityThreshold: 40,
    maxDailyWater: 500,
    irrigationDuration: 30,
    
    // Schedule Settings
    scheduleEnabled: true,
    morningTime: '07:00',
    eveningTime: '19:00',
    weekendMode: false,
    
    // Sensor Settings
    sensorFrequency: 5,
    tempUnit: 'celsius',
    alertsEnabled: true,
    criticalHumidity: 20,
    
    // System Settings
    autoUpdates: true,
    dataRetention: 30,
    notifications: {
      email: true,
      sms: false,
      push: true
    },
    
    // Advanced Settings
    debugMode: false,
    mqttEnabled: true,
    apiEndpoint: 'http://localhost:3000',
    deviceName: 'SmartGrow-001'
  });

  const [unsavedChanges, setUnsavedChanges] = useState(false);
  const [saveStatus, setSaveStatus] = useState('');

  const handleConfigChange = (key, value) => {
    if (key.includes('.')) {
      const [parent, child] = key.split('.');
      setConfig(prev => ({
        ...prev,
        [parent]: {
          ...prev[parent],
          [child]: value
        }
      }));
    } else {
      setConfig(prev => ({
        ...prev,
        [key]: value
      }));
    }
    setUnsavedChanges(true);
    setSaveStatus('');
  };

  const handleSave = async () => {
    setSaveStatus('saving');
    
    // Simulate API call
    try {
      await new Promise(resolve => setTimeout(resolve, 1500));
      
      // Here you would make actual API call
      console.log('Saving configuration:', config);
      
      setSaveStatus('success');
      setUnsavedChanges(false);
      
      setTimeout(() => setSaveStatus(''), 3000);
    } catch (error) {
      setSaveStatus('error');
      setTimeout(() => setSaveStatus(''), 3000);
    }
  };

  const handleReset = () => {
    if (window.confirm('√ätes-vous s√ªr de vouloir r√©initialiser toutes les configurations ?')) {
      // Reset to default values
      setConfig({
        autoIrrigation: true,
        humidityThreshold: 40,
        maxDailyWater: 500,
        irrigationDuration: 30,
        scheduleEnabled: true,
        morningTime: '07:00',
        eveningTime: '19:00',
        weekendMode: false,
        sensorFrequency: 5,
        tempUnit: 'celsius',
        alertsEnabled: true,
        criticalHumidity: 20,
        autoUpdates: true,
        dataRetention: 30,
        notifications: {
          email: true,
          sms: false,
          push: true
        },
        debugMode: false,
        mqttEnabled: true,
        apiEndpoint: 'http://localhost:3000',
        deviceName: 'SmartGrow-001'
      });
      setUnsavedChanges(true);
    }
  };

  const exportConfig = () => {
    const dataStr = JSON.stringify(config, null, 2);
    const dataBlob = new Blob([dataStr], { type: 'application/json' });
    const url = URL.createObjectURL(dataBlob);
    const link = document.createElement('a');
    link.href = url;
    link.download = 'smartgrow-config.json';
    link.click();
    URL.revokeObjectURL(url);
  };

  const importConfig = (event) => {
    const file = event.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onload = (e) => {
        try {
          const importedConfig = JSON.parse(e.target.result);
          setConfig(importedConfig);
          setUnsavedChanges(true);
          alert('Configuration import√©e avec succ√®s!');
        } catch (error) {
          alert('Erreur lors de l\'importation de la configuration');
        }
      };
      reader.readAsText(file);
    }
  };

  return (
    <main className="configurations-page">
      <header className="page-header">
        <h1>Configuration du Syst√®me</h1>
        <p className="page-subtitle">Personnalisez les param√®tres de votre syst√®me d'irrigation</p>
        
        {unsavedChanges && (
          <div className="unsaved-changes-banner">
            ‚ö†Ô∏è Vous avez des modifications non sauvegard√©es
          </div>
        )}
      </header>

      <div className="config-container">
        <div className="config-content">
          
          {/* Irrigation Settings */}
          <ConfigSection title="Param√®tres d'Irrigation" icon="üíß">
            <SettingItem 
              label="Irrigation Automatique"
              description="Activer l'irrigation automatique bas√©e sur les capteurs"
            >
              <Toggle 
                checked={config.autoIrrigation}
                onChange={(e) => handleConfigChange('autoIrrigation', e.target.checked)}
              />
            </SettingItem>

            <SettingItem 
              label="Seuil d'Humidit√© (%)"
              description="D√©clencher l'irrigation quand l'humidit√© descend en dessous de ce seuil"
            >
              <div className="slider-container">
                <input
                  type="range"
                  min="10"
                  max="80"
                  value={config.humidityThreshold}
                  onChange={(e) => handleConfigChange('humidityThreshold', parseInt(e.target.value))}
                  className="slider"
                />
                <span className="slider-value">{config.humidityThreshold}%</span>
              </div>
            </SettingItem>

            <SettingItem 
              label="Consommation Maximale (L/jour)"
              description="Limite de consommation d'eau par jour"
            >
              <input
                type="number"
                value={config.maxDailyWater}
                onChange={(e) => handleConfigChange('maxDailyWater', parseInt(e.target.value))}
                className="number-input"
                min="100"
                max="2000"
              />
            </SettingItem>

            <SettingItem 
              label="Dur√©e d'Irrigation (minutes)"
              description="Dur√©e par d√©faut de chaque session d'irrigation"
            >
              <input
                type="number"
                value={config.irrigationDuration}
                onChange={(e) => handleConfigChange('irrigationDuration', parseInt(e.target.value))}
                className="number-input"
                min="5"
                max="120"
              />
            </SettingItem>
          </ConfigSection>

          {/* Schedule Settings */}
          <ConfigSection title="Programmation Horaire" icon="‚è∞">
            <SettingItem 
              label="Programmation Activ√©e"
              description="Programmer des heures fixes d'irrigation"
            >
              <Toggle 
                checked={config.scheduleEnabled}
                onChange={(e) => handleConfigChange('scheduleEnabled', e.target.checked)}
              />
            </SettingItem>

            <SettingItem 
              label="Irrigation Matinale"
              description="Heure de l'irrigation matinale"
            >
              <input
                type="time"
                value={config.morningTime}
                onChange={(e) => handleConfigChange('morningTime', e.target.value)}
                className="time-input"
                disabled={!config.scheduleEnabled}
              />
            </SettingItem>

            <SettingItem 
              label="Irrigation Vesp√©rale"
              description="Heure de l'irrigation du soir"
            >
              <input
                type="time"
                value={config.eveningTime}
                onChange={(e) => handleConfigChange('eveningTime', e.target.value)}
                className="time-input"
                disabled={!config.scheduleEnabled}
              />
            </SettingItem>

            <SettingItem 
              label="Mode Week-end"
              description="D√©sactiver la programmation les week-ends"
            >
              <Toggle 
                checked={config.weekendMode}
                onChange={(e) => handleConfigChange('weekendMode', e.target.checked)}
                disabled={!config.scheduleEnabled}
              />
            </SettingItem>
          </ConfigSection>

          {/* Sensor Settings */}
          <ConfigSection title="Configuration des Capteurs" icon="üìä">
            <SettingItem 
              label="Fr√©quence de Lecture (minutes)"
              description="Intervalle entre les lectures des capteurs"
            >
              <select
                value={config.sensorFrequency}
                onChange={(e) => handleConfigChange('sensorFrequency', parseInt(e.target.value))}
                className="select-input"
              >
                <option value={1}>1 minute</option>
                <option value={5}>5 minutes</option>
                <option value={10}>10 minutes</option>
                <option value={15}>15 minutes</option>
                <option value={30}>30 minutes</option>
              </select>
            </SettingItem>

            <SettingItem 
              label="Unit√© de Temp√©rature"
              description="Choisir l'unit√© d'affichage de la temp√©rature"
            >
              <select
                value={config.tempUnit}
                onChange={(e) => handleConfigChange('tempUnit', e.target.value)}
                className="select-input"
              >
                <option value="celsius">Celsius (¬∞C)</option>
                <option value="fahrenheit">Fahrenheit (¬∞F)</option>
              </select>
            </SettingItem>

            <SettingItem 
              label="Alertes Activ√©es"
              description="Recevoir des notifications en cas de probl√®me"
            >
              <Toggle 
                checked={config.alertsEnabled}
                onChange={(e) => handleConfigChange('alertsEnabled', e.target.checked)}
              />
            </SettingItem>

            <SettingItem 
              label="Seuil d'Alerte Critique (%)"
              description="D√©clencher une alerte urgente si l'humidit√© descend en dessous"
            >
              <div className="slider-container">
                <input
                  type="range"
                  min="5"
                  max="30"
                  value={config.criticalHumidity}
                  onChange={(e) => handleConfigChange('criticalHumidity', parseInt(e.target.value))}
                  className="slider critical"
                  disabled={!config.alertsEnabled}
                />
                <span className="slider-value">{config.criticalHumidity}%</span>
              </div>
            </SettingItem>
          </ConfigSection>

          {/* Notifications Settings */}
          <ConfigSection title="Notifications" icon="üîî">
            <SettingItem 
              label="Notifications Email"
              description="Recevoir des alertes par email"
            >
              <Toggle 
                checked={config.notifications.email}
                onChange={(e) => handleConfigChange('notifications.email', e.target.checked)}
              />
            </SettingItem>

            <SettingItem 
              label="Notifications SMS"
              description="Recevoir des alertes par SMS"
            >
              <Toggle 
                checked={config.notifications.sms}
                onChange={(e) => handleConfigChange('notifications.sms', e.target.checked)}
              />
            </SettingItem>

            <SettingItem 
              label="Notifications Push"
              description="Recevoir des notifications push sur l'appareil"
            >
              <Toggle 
                checked={config.notifications.push}
                onChange={(e) => handleConfigChange('notifications.push', e.target.checked)}
              />
            </SettingItem>
          </ConfigSection>

          {/* System Settings */}
          <ConfigSection title="Param√®tres Syst√®me" icon="‚öôÔ∏è">
            <SettingItem 
              label="Mises √† Jour Automatiques"
              description="Installer automatiquement les mises √† jour du syst√®me"
            >
              <Toggle 
                checked={config.autoUpdates}
                onChange={(e) => handleConfigChange('autoUpdates', e.target.checked)}
              />
            </SettingItem>

            <SettingItem 
              label="R√©tention des Donn√©es (jours)"
              description="Dur√©e de conservation des donn√©es historiques"
            >
              <select
                value={config.dataRetention}
                onChange={(e) => handleConfigChange('dataRetention', parseInt(e.target.value))}
                className="select-input"
              >
                <option value={7}>7 jours</option>
                <option value={30}>30 jours</option>
                <option value={90}>90 jours</option>
                <option value={365}>1 an</option>
              </select>
            </SettingItem>

            <SettingItem 
              label="Nom du Dispositif"
              description="Identifiant unique de votre syst√®me SmartGrow"
            >
              <input
                type="text"
                value={config.deviceName}
                onChange={(e) => handleConfigChange('deviceName', e.target.value)}
                className="text-input"
                placeholder="SmartGrow-001"
              />
            </SettingItem>
          </ConfigSection>

          {/* Advanced Settings */}
          <ConfigSection title="Param√®tres Avanc√©s" icon="üîß">
            <SettingItem 
              label="Mode Debug"
              description="Activer les logs d√©taill√©s pour le d√©pannage"
            >
              <Toggle 
                checked={config.debugMode}
                onChange={(e) => handleConfigChange('debugMode', e.target.checked)}
              />
            </SettingItem>

            <SettingItem 
              label="Protocole MQTT"
              description="Utiliser MQTT pour la communication avec l'ESP32"
            >
              <Toggle 
                checked={config.mqttEnabled}
                onChange={(e) => handleConfigChange('mqttEnabled', e.target.checked)}
              />
            </SettingItem>

            <SettingItem 
              label="Point d'Acc√®s API"
              description="URL du serveur backend"
            >
              <input
                type="url"
                value={config.apiEndpoint}
                onChange={(e) => handleConfigChange('apiEndpoint', e.target.value)}
                className="text-input"
